    # Define the list of stock prices and the moving average period
    stock_prices = [205, 210, 215, 220, 222, 225, 230, 235, 236, 240]
    period = 5

    # Calculate the 5-day moving average for the last available day in the data set
    moving_averages = []
    for i in range(len(stock_prices)):
        if i < period - 1:
            moving_averages.append(None)
        else:
            window = stock_prices[i - period + 1:i + 1]
            moving_averages.append(sum(window) / period)

    # Get the moving average on the 10th trading day
    result = moving_averages[-1]  # The last element in the moving_averages list

    # Return the final answer rounded to two decimal places
    return round(result, 2)

